mapper.select:
SELECT A.*, M.nickname AS extra__writer FROM article AS A INNER JOIN member AS M ON A.memberId = M.id WHERE A.displayStatus = 1 AND A.boardId= #{id}
AND A.title LIKE CONCAT('%',#{searchKeyword},'%')
AND A.body LIKE CONCAT('%',#{searchKeyword},'%')
AND (title LIKE CONCAT('%',#{searchKeyword},'%') OR A.body LIKE CONCAT('%',#{searchKeyword},'%'))
ORDER BY A.id DESC
LIMIT #{limitFrom}, #{limitCount}

SELECT * FROM article WHERE seq = #{seq}
UNION ALL (
SELECT * FROM
articleWHERE seq < #{seq}
ORDER BY seq DESC LIMIT 2)
UNION ALL (
SELECT * FROM article WHERE seq > #{seq}
ORDER BY seq ASC LIMIT 2)
ORDER BY seq DESC;

mapper.update:
UPDATE article
updateDate = NOW(), title = #{title}, body = #{body},
WHERE id = #{id}
