mapper.select:
SELECT *
FROM member
WHERE name = #{name}
AND email = #{email}

mapper.update:
UPDATE `member`
SET updateDate = NOW(),
loginPw = #{loginPw}
WHERE id = #{actorId}

UPDATE member
updateDate = NOW(), `name` = #{name}, `nickname` = #{nickname}, `email` = #{email}, `cellphoneNo` = #{cellphoneNo}, `loginPw` = #{loginPw},
WHERE id = #{id}
